import React from 'react';
import { Grid, Paper, Typography } from '@mui/material';
import { Link } from 'react-router-dom'; // リンクを追加するために使用する
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Home from './pages/Home';
// import About from './pages/About';
// import Contact from './pages/Contact';
import Header from './components/Header';
import Footer from './components/Footer';
import Content1Page from './pages/content1';
import Sidebar from './components/Sidebar';



// const handleClick = () => {
//   // クリック時に移動するURLにリダイレクトする処理を追加
//   window.location.href = '/content1';
// };

const RectangleComponent = ({ title, to }) => (
  <Grid item xs={5} sm={7} md={7} lg={7} xl={2} style={{ textAlign: 'center' }}>
  <Paper elevation={10} style={{ height: 100, margin: 10, display: 'flex', alignItems: 'center', justifyContent: 'center', padding: 20, backgroundColor: '#37474f', borderRadius: '15px', cursor: 'pointer' }}>
  {/* <Link to={to} style={{ textDecoration: 'none', color: 'inherit' }}> */}
        <Typography variant="h4" style={{ color: 'white', fontWeight: 'bold' }}>{title}</Typography>
  {/* </Link> */}
    {/* ここにコンポーネントの内容を追加 */}
  </Paper>
  </Grid>
);

const TitleComponent = () => (
  <Grid item xs={12} style={{ height: 500 }}>
          <div style={{ textAlign: 'center' }}>
            <img src="/images/airplane.jpg" style={{ width: '100%', height: '70%', objectFit: 'cover', position: 'absolute', top: 0, left: 0, zIndex: -1, opacity: 0.5 }} />
            <Typography variant="h1" style={{ color: 'black', fontWeight: 'bold' }}>セクションのタイトル</Typography>
          </div>
      </Grid>
);

const App = () => {
  return (
    <Router>
    <div>
    <Header />
    <Grid container spacing={6} justifyContent="center" style={{ marginTop: 64, marginBottom: 64 }}>

        <TitleComponent />

        <Routes> {/* Routesでルーティングを定義 */}
            <Route path="/" element={<Home />} />
            <Route path="/content1" element={<Content1Page />} /> {/* Content1ページのルート */}
            <Route path="/content2" element={<Content1Page />} /> {/* Content1ページのルート */}
            <Route path="/content3" element={<Content1Page />} /> {/* Content1ページのルート */}
            {/* 他のページのルートを追加 */}
          </Routes>
          <Link to="/content1">Link to Products</Link>
          <RectangleComponent title="コンポーネント1" to="/content1" />
          <RectangleComponent title="コンポーネント2" to="/content2" />
          <RectangleComponent title="コンポーネント3" to="/content3" />
          <RectangleComponent title="コンポーネント4" to="/content4" />
    </Grid>
    <Footer />
    </div>
    </Router>
  );
};

export default App;
